import React, { useEffect, useState } from 'react';

import NewsItem from './NewsItem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';
import InfiniteScroll from 'react-infinite-scroll-component';

const News = (props) => {
    const apiKey = process.env.REACT_APP_NEWS_API
    const [articles, setArticles] = useState([])
    const [loading, setLoading] = useState(true)
    const [page, setPage] = useState(1)
    const [totalResults, setTotalResults] = useState(0)
    const capitalizeFirstLetter = (string) => {
        return string.charAt(0).toUpperCase() + string.slice(1);
        //   document.title = `${capitalizeFirstLetter(props.category)} - NewsSnake`;
    };

    const updateNews = async () => {
        props.setProgress(10);
        const url = `https://newsapi.org/v2/top-headlines?&category=${props.category}&apiKey=22a7d4bce95b478a9443af88bf9adb35&page=${page}&pageSize=${props.pageSize}`;
        setLoading(true);
        let data = await fetch(url);
        props.setProgress(30);
        // await = if i wants to convert it into JSON or text then await is use.
        let parsedData = await data.json()
        props.setProgress(70);
        setArticles(parsedData.articles)
        setTotalResults(parsedData.totalResults)
        setLoading(false)
        props.setProgress(100);


    }
    useEffect(() => {
        document.title = `${capitalizeFirstLetter(props.category)} - NewsSnake`;

        updateNews();
    }, [])
    // async  : wait to resolve some promises , after it'll run .

    // const handlePrevClick = async () => {

    //     seytPage(page - 1);
    //     updateNews();
    // }
    // const handleNextClick = async () => {
    //     setPage(page + 1);
    //     updateNews();
    // }
    const fetchMoreData = async () => {
        const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=22a7d4bce95b478a9443af88bf9adb35`;
        // 22a7d4bce95b478a9443af88bf9adb35
        // const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&sources=techcrunch&apiKey=${props.apiKey}&page=${page + 1}&pageSize=${props.pageSize}`;
        // const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${page + 1}&pageSize=${props.pageSize}`;
        setPage(page + 1);
        let data = await fetch(url);
        let parsedData = await data.json()
        setArticles(articles.concat(parsedData.articles))
        setTotalResults(parsedData.totalResults)
    };


    return (
        <>
            <h2 className="text-center" src="/NewsBack.png"
                style={{ backgroundColor: 'white', color: 'black', margin: '66px 0px' }} >
                NewsSnake - Top {capitalizeFirstLetter(props.category)} Headlines  </h2>
            {loading && <Spinner />}
            <InfiniteScroll
                dataLength={articles.length} //This is important field to render the next data
                next={fetchMoreData}
                hasMore={articles.length !== totalResults}
                loader={<Spinner />}
            >
                <div className="container">
                    <div className="row">
                        {articles.map((element) => {
                            return <div className="col-md-4" key={element.url}>
                                <NewsItem title={element.title ? element.title : ""} description={element.description ? element.description.slice(0, 88) : ""} newsUrl={element.url} imageurl={element.urlToImage} author={element.author} date={element.publishedAt} source={element.source.name} />
                            </div>
                        })}

                    </div>
                </div>
            </InfiniteScroll>
        </>
    )

};
News.defaultProps =
{
    country: 'in',
    pageSize: 8,
    category: 'general',
}
News.propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    country: PropTypes.string,
}
export default News;
