import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'

import React, { Component } from 'react'
import Navbar from './components/Navbar'
import News from './components/News'
import LoadingBar from 'react-top-loading-bar'

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
} from "react-router-dom";
export default class App extends Component {
  pageSize = 5;
  state = {
    progress: 0
  }
  setProgress(progress) {
    this.setState({ progress: this.state.progress })
  }
  render() {
    return (
      <div>
        <Router>
          <Navbar />
          <LoadingBar
            color='#f11946'
            progress={this.state.progress}
          // onLoaderFinished={() => setProgress(0)}
          />
          <Switch>
            <Route exact path="/"><News setProgress={this.setProgress} setProgress={setProgress} setProgress={setProgress} key='general' pageSize={5} country="in" category="general" /></Route>
            <Route exact path="/science"><News setProgress={this.setProgress} setProgress={setProgress} setProgress={setProgress} key='science' pageSize={5} country="in" category="science" /></Route>
            <Route exact path="/business"><News setProgress={this.setProgress} setProgress={setProgress} setProgress={setProgress} key='business' pageSize={5} country="in" category="business" /></Route>
            <Route exact path="/entertainment"><News setProgress={this.setProgress} setProgress={setProgress} setProgress={setProgress} key='Entertainment' pageSize={5} country="in" category="Entertainment" /></Route>
            <Route exact path="/health"><News setProgress={this.setProgress} setProgress={setProgress} setProgress={setProgress} key='Health' pageSize={5} country="in" category="Health" /></Route>
            <Route exact path="/sports"><News setProgress={this.setProgress} setProgress={setProgress} setProgress={setProgress} key='Sports' pageSize={5} country="in" category="Sports" /></Route>
            <Route exact path="/technology"><News setProgress={this.setProgress} setProgress={setProgress} setProgress={setProgress} key='Technology' pageSize={5} country="in" category="Technology" /></Route>

          </Switch>
        </Router>
      </div>
    )
  }

}
