import React, { Component } from 'react';
import NewsItem from './NewsItem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';
import InfiniteScroll from 'react-infinite-scroll-component';

export default class News extends Component {

    static defaultProps =
        {
            country: 'in',
            pageSize: 8,
            category: 'general',
        }
    static propTypes = {
        country: PropTypes.string,
        pageSize: PropTypes.number,
        country: PropTypes.string,
    }
    capitalizeFirstLetter = (string) => {
        return string.charAt(0).toUpperCase() + string.slice(1);
    }
    constructor(props) {
        super(props);
        console.log("Hey, i am constructor from news component ");
        this.state = {
            articles: [],
            loading: false,
            page: 1,
        }
        document.title = `${this.capitalizeFirstLetter(props.category)} - NewsSnake`;
    }
    async updateNews() {
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=a193ceadd4874a3c962f25e599c29c2d&page=${this.state.page}&pageSize=${this.props.pageSize}`;
        this.setState({ loading: true })
        let data = await fetch(url);          // await = if i wants to convert it into JSON or text then await is use.
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({
            articles: parsedData.articles,
            totalResults: parsedData.totalResults,
            loading: false,
        })

    }
    async componentDidMount() {                   // async  : wait to resolve some promises , after it'll run .
        this.updateNews();

    }
    handlePrevClick = async () => {

        this.setState({ page: this.state.page - 1 });
        this.updateNews();
    }
    handleNextClick = async () => {
        this.setState({ page: this.state.page + 1 });
        this.updateNews();
    }
    fetchMoreData = async () => {
        this.setState({ page: this.state.page + 1 });
        const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apikey=dbe57b028aeb41e285a226a94865f7a7&page=${this.state.page}&pageSize=${this.props.pageSize}`;
        let data = await fetch(url);
        let parsedData = await data.json()
        this.setState({
            articles: this.state.articles.concat(parsedData.articles),
            totalResults: parsedData.totalResults,
            loading: false,
        })
    };



    render() {
        console.log("render");
        return (
            <div className="container my-3">
                <h2 className="text-center" src="/NewsBack.png" style={{ backgroundColor: 'white', color: 'black', margin: '35px 0px' }} >
                    NewsSnake - Top {this.capitalizeFirstLetter(this.props.category)} Headlines  </h2>
                {this.state.loading && <Spinner />}
                <InfiniteScroll
                    dataLength={items.length} //This is important field to render the next data
                    next={fetchData}
                    hasMore={true}
                >
                    <div className="row">
                        {this.state.articles.map((element) => {
                            return <div className="col-md-4" key={element.url}>
                                <NewsItem title={element.title ? element.title.slice(0, 45) : ""} description={element.description ? element.description.slice(0, 88) : ""} newsUrl={element.url} imageurl={element.urlToImage} author={element.author} date={element.publishedAt} source={element.source.name} />
                            </div>

                        })}

                    </div>
                    <div className="container" className="d-flex justify-content-between">
                        <button disabled={this.state.page <= 1} type="button" className="btn btn-primary" onClick={this.handlePrevClick}> &larr;Previous</button>
                        <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults / 20)} type="button" className="btn btn-primary" onClick={this.handleNextClick}>Next &rarr;</button>
                    </div>
                </InfiniteScroll>
            </div >
        )
    }
}
