import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class News extends Component {

    constructor() {
        super();
        console.log("Hey, i am constructor from news component ");
        this.state = {
            articles: [],
            loading: false
        }
    }
    async componentDidMount() {                   // async wait to resolve some promises , after it'll run .
        console.log("cdn");
        let url = "https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=a193ceadd4874a3c962f25e599c29c2d&page=1";
        let data = await fetch(url);          // await = if i wants to convert it into JSON or text then await is use.
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({ articles: parsedData.articles })
        console.log(data);
    }
    render() {
        console.log("render")
        return (
            <div className="container my-3">
                <h2>NewsSnake - Top Headlines </h2>
                <div className="row">
                    {this.state.articles.map((element) => {
                        return <div className="col-md-4" key={element.url}>
                            <NewsItem title={element.title ? element.title.slice(0, 45) : ""} description={element.description ? element.description.slice(0, 88) : ""} newsUrl={element.url} imageurl={element.urlToImage} />
                        </div>

                    })}

                </div>
                <div className="container">
                    <button type="button" class="btn btn-primary">Previous</button>
                    <button type="button" class="btn btn-secondary">Next</button>
                </div>
            </div>
        )
    }
}
