import React, { Component } from 'react'

export class NewsItem extends Component {

    render() {
        let { title, description, imageurl, newsUrl } = this.props;               // destruturing : 
        return (
            <div className=" my-3 mx-3 ">
                <div className="card" style={{ width: "18rem" }}>
                    <img src={!imageurl ? "https://www.espncricinfo.com/story/sidharth-monga-what-we-learned-from-watching-the-1992-world-cup-final-in-full-again-1219932" : imageurl} className="card-img-top" alt="..." />
                    <div className="card-body">
                        <h5 className="card-title">{title} <>...</></h5>
                        <p className="card-text">{description} <b>Read more...</b></p>
                        <a rel="noreferrer" href={newsUrl} target="_blank" className="btn btn-primary">Read News</a>
                    </div>
                </div>
            </div>
        )
    }
}
export default NewsItem;
