import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './components/Spinner'
export default class News extends Component {

    constructor() {
        super();
        console.log("Hey, i am constructor from news component ");
        this.state = {
            articles: [],
            loading: false,
            page: 1,
        }
    }
    async componentDidMount() {                   // async  : wait to resolve some promises , after it'll run .
        let url = `https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=a193ceadd4874a3c962f25e599c29c2d&page=${this.statepage - 1}&pageSize=${this.props.pageSize}`;
        let data = await fetch(url);          // await = if i wants to convert it into JSON or text then await is use.
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({
            articles: parsedData.articles,
            totalResults: parsedData.totalResults,
            loading: false


        })
    }
    handlePrevClick = async () => {

        console.log("Go to Previous Page");
        let url = `https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=a193ceadd4874a3c962f25e599c29c2d&page=${this.statepage - 1}&pageSize=${this.props.pageSize}`;
        this.setState({ loading: true })
        let data = await fetch(url);          // await = if i wants to convert it into JSON or text then await is use.
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({
            page: this.state.page - 1,
            articles: parsedData.articles,
            loading: false


        })
    }
    handleNextClick = async () => {
        console.log("Go to Next Page");
        if (this.state.page + 1 > Math.ceil(this.state.totalResults / this.props.pageSize)) {
        }
        else {

            let url = `https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=a193ceadd4874a3c962f25e599c29c2d&page=${this.statepage + 1}&pageSize=${this.props.pageSize}`;
            this.setState({ loading: false })
            let data = await fetch(url);          // await = if i wants to convert it into JSON or text then await is use.
            let parsedData = await data.json()
            console.log(parsedData);
            this.setState({
                page: this.state.page + 1,
                articles: parsedData.articles,
                loading: false

            })
        }
    }
    render() {
        console.log("render");

        return (
            <div className="container my-3">
                <h2 classsName="text-center">NewsSnake - Top Headlines </h2>
                {this.state.loading && <Spinner />}
                <div className="row">
                    {!this.state.loading && this.state.articles.map((element) => {
                        return <div className="col-md-4" key={element.url}>
                            <NewsItem title={element.title ? element.title.slice(0, 45) : ""} description={element.description ? element.description.slice(0, 88) : ""} newsUrl={element.url} imageurl={element.urlToImage} />
                        </div>

                    })}

                </div>
                <div className="container" className="d-flex justify-content-between">
                    <button disabled={this.state.page <= 1} type="button" className="btn btn-primary" onClick={this.handlePrevClick}> &larr;Previous</button>
                    <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults / 20)} type="button" className="btn btn-primary" onClick={this.handleNextClick}>Next &rarr;</button>
                </div>
            </div>
        )
    }
}
